<script>import arrSorted from '~/mixins/arr_sorted.js';export default {  name: "BoardType",  mixins: [arrSorted],  props: ["board_type", "to"],  data() {    return {      selected: '',      bt: {}    }  },  computed: {    board: {      get() {        const sortedRooms = Object.values(this.bt).slice().sort(this.compare);        return sortedRooms;      },      set(value) {        this.bt = {...value};      }    },  },  created() {    for (const key in this.board_type) {      if (this.board_type[key]['extra_charge'] == null || this.board_type[key]['extra_charge'] === 0) {        this.board_type[key]["selected"] = true;        this.selected = this.board_type[key]["id"];        this.$emit('set_board_price', this.board_type[key]['extra_charge']);        this.$emit('set_board_id', this.board_type[key]["id"]);        this.$emit('set_name_board', this.board_type[key]["name"]);      }    }    this.bt = {...this.board_type};  },  methods: {    setRoom(selected) {      for (const key in this.bt) {        if (this.bt[key]["id"] == selected) {          this.bt[key]["selected"] = true;          this.selected = this.bt[key]["id"]          this.$emit('set_board_price', this.bt[key]['extra_charge']);          this.$emit('set_board_id', this.bt[key]["id"]);          this.$emit('set_name_board', this.bt[key]["name"]);          this.$emit('set_click', true);        } else {          if (this.bt[key]["selected"])            this.bt[key]["selected"] = false;        }      }      this.board = this.bt;    }  }}</script><template>  <div class="dynamic">    <div      v-for="(item, index) in board"      :key="index"      :class="{'selected': item['selected']}"      @click="setRoom(item['id'])"      class="option">      <div class="input">        <input          v-model="selected"          :checked="item['selected']"          :value="item['id']"          type="radio">      </div>      <label        v-if="item['extra_charge'] > 0"        :for="'board_type-1-' + index + to">      						<span class="price ">+₽{{ item["extra_charge"] }}<div class="price_type">/сутки </div>      						</span>        <span class="name">{{ item["name"] }}</span>      </label>      <label        v-else        :for="'board_type-1-' + index + to">        <span class="price best">Включено</span><span class="name">{{ item["name"] }}</span>      </label>    </div>  </div></template><style scoped></style>