<script>export default {  name: "PaxSelectors",  props: ['sale'],  data() {    return {      adults: 2,      children: 0,      infants: 0,    }  },  methods: {    increment(person) {      switch (person) {        case 'children':          if (this.children < this.sale['max_room_capacity_children'])            this.children++;          break;        case 'adults':          if (this.adults < this.sale['max_room_capacity_adults'])            this.adults++;          break;        case 'infants':          if (this.children < this.sale['max_room_capacity_infants'])            this.infants++;          break;      }      this.$emit('pax', {        adults: this.adults,        children: this.children,        infants: this.infants,      });    },    decrement(person) {      switch (person) {        case 'children':          if (this.children > 0)            this.children--;          break;        case 'adults':          if (this.adults > 1)            this.adults--;          break;        case 'infants':          if (this.infants > 0)            this.infants--;          break;      }      this.$emit('pax', {        adults: this.adults,        children: this.children,        infants: this.infants,      });    },  }}</script><template>  <div class="pax-selectors-container">    <div      v-if="sale['max_room_capacity_adults'] > 0"      class="pax-type">      <div class="pax-type-label">Взрослые</div>      <span class="number-picker">			<button        @click="decrement('adults')"        class="decrease "> - </button>			<span class="value">{{ adults }}</span>			<button        @click="increment('adults')"        class="increase "> + </button>			</span>    </div>    <div      v-if="sale['max_room_capacity_children'] > 0"      class="pax-type">      <div class="pax-type-label">Дети <span        class="age-details">(2-11 лет)</span>      </div>      <span class="number-picker">			<button        @click="decrement('children')"        class="decrease disabled"> - </button>			<span class="value">{{ children }}</span>			<button        @click="increment('children')"        class="increase "> + </button>				</span>    </div>    <div      v-if="sale['max_room_capacity_infants'] > 0"      class="pax-type">      <div class="pax-type-label">Младенцы <span class="age-details">(&lt;2года)</span>      </div>      <span class="number-picker">			<button        @click="decrement('infants')"        class="decrease disabled"> - </button>			<span class="value">{{ infants }}</span>			<button        @click="increment('infants')"        class="increase "> + </button>			</span>    </div>  </div></template><style scoped></style>