<script>import arrSorted from '~/mixins/arr_sorted.js';export default {  name: "RoomType",  mixins: [arrSorted],  props: ["room_type", "to"],  data() {    return {      selected: '',      rt: {}    }  },  computed: {    room: {      get() {        const sortedRooms = Object.values(this.rt).slice().sort(this.compare);        return sortedRooms;      },      set(value) {        this.rt = {...value};      }    },  },  created() {    for (const key in this.room_type) {      if (this.room_type[key]['extra_charge_for_room'] == null || this.room_type[key]['extra_charge_for_room'] === 0) {        this.room_type[key]["selected"] = true;        this.selected = this.room_type[key]["id"];        this.$emit('set_price', this.room_type[key]['extra_charge_for_room']);        this.$emit('set_id', this.room_type[key]["id"]);        this.$emit('set_name_room', this.room_type[key]["name"]);      }    }    this.rt = {...this.room_type};  },  methods: {    setRoom(selected) {      for (const key in this.rt) {        if (this.rt[key]["id"] == selected) {          this.rt[key]["selected"] = true;          this.selected = this.rt[key]["id"]          this.$emit('set_price', this.rt[key]['extra_charge_for_room']);          this.$emit('set_id', this.rt[key]["id"]);          this.$emit('set_name_room', this.rt[key]["name"]);        } else {          if (this.rt[key]["selected"])            this.rt[key]["selected"] = false;        }      }      this.room = this.rt;    }  }}</script><template>  <div class="dynamic">    <div      v-for="(item, index) in room"      :key="index"      :class="{'selected': item['selected']}"      @click="setRoom(item['id'])"      class="option">      <div class="input">        <input          v-model="selected"          :value="item['id']"          type="radio">      </div>      <label        v-if="item['extra_charge_for_room'] > 0"        :for="'room_type-1-' + index + to">      						<span class="price ">+₽{{ item["extra_charge_for_room"] }}<div class="price_type"> /сутки </div>      						</span>        <span class="name">{{ item["name"] }}</span>      </label>      <label        v-else        :for="'room_type-1-' + index + to">        <span class="price best">Включено</span><span class="name">{{ item["name"] }}</span>      </label>    </div>  </div></template><style scoped></style>