<script>import arrSorted from '~/mixins/arr_sorted.js';export default {  name: "TransferType",  mixins: [arrSorted],  props: ["transfer_type", "to"],  data() {    return {      selected: '',      bt: {}    }  },  computed: {    board: {      get() {        const sortedRooms = Object.values(this.bt).slice().sort(this.compare);        return sortedRooms;      },      set(value) {        this.bt = {...value};      }    },  },  created() {    for (const key in this.transfer_type) {      if (this.transfer_type[key]['extra_charge'] == null) {        this.transfer_type[key]["selected"] = true;        this.selected = this.transfer_type[key]["id"];      }    }    this.bt = {...this.transfer_type};  },  methods: {    setTransfer(selected) {      for (const key in this.bt) {        if (this.bt[key]["id"] == selected) {          this.bt[key]["selected"] = true;          this.selected = this.bt[key]["id"]          this.$emit('set_transfer_price', this.bt[key]['extra_charge']);          this.$emit('set_transfer_id', this.bt[key]["id"]);        } else {          if (this.bt[key]["selected"])            this.bt[key]["selected"] = false;        }      }      this.board = this.bt;    }  }}</script><template>  <div class="dynamic">    <div      v-for="(item, index) in board"      :key="index"      :class="{'selected': item['selected']}"      @click="setTransfer(item['id'])"      class="option">      <div class="input">        <input          v-model="selected"          :value="item['id']"          type="radio" :id="'transfer_type-1-' + index + to" :name="'transfer_type-1-' + index">      </div>      <label        v-if="item['extra_charge'] > 0"        :for="'transfer_type-1-' + index + to">      						<span class="price ">+₽{{ item["extra_charge"] }}<div class="price_type">/чел </div>      						</span>        <span class="name">{{ item["name"] }}</span>      </label>      <label        v-else        :for="'transfer_type-1-' + index + to">        <span class="price best">Включено</span><span class="name">{{ item["name"] }}</span>      </label>    </div>  </div></template><style scoped></style>