<template>  <div>    <cookie-banner/>    <div v-show="!load" style="height: 700px; display: flex; align-items: center; justify-content: center">      <voyage-spinner/>    </div>    <div class="main" v-show="load" style="display: none">      <div id="headerPosition"></div>      <header-contact/>      <nuxt/>      <!-- footer -->      <footer-all/>      <client-only>        <script>          // выбор суммы подарочной карты          $(".input-amount").click(function () {            $(".input-amount").removeClass("selected");            $(this).toggleClass("selected");          });          // показать, спрятать          $(".seeMore").click(function () {            $(".seeMoreDetails").toggle(200);            $(".show").toggle(0);            $(".hide").toggle(0);          });          // закрыть куки          $(".Button--accept, .Button--reject").click(function () {            $("#cookieBanner").hide();          });          // переключение вкладок          $(".TabsTransport__wrapper").click(function () {            if ($(this).hasClass('is-current')) {            } else {              $(".TabsTransport__wrapper").toggleClass("is-current");              $(".ProposalsPanel").toggle();            }          });          // открыть подробное описание          $(".MoreInfos__button").click(function () {            $(this).toggleClass("rotate-icon");            $(this).closest('.CardTransport__main').next(".CardTransportDetails").toggle();          });          $(".data-text").click(function () {            $(this).closest('.section-data').find(".all-text").toggle();          });          $(".add").click(function () {            $(this).toggle();            $(this).closest('.container__actions').find(".remove-price").toggle();            $(this).closest('.container').next(".option-details").toggle();          });          $(".button--link").click(function () {            $(this).closest('.container__actions').find(".button--secondary").toggle();            $(this).closest('.remove-price').toggle();            $(this).closest('.container').next(".option-details").toggle();          });          // подробное описание страховки          $(".Guarantee__name").click(function () {            $(this).next(".collapse").toggle("200");          });          // показать полную сумму          $(".SummaryButtonQuotationDetail").click(function () {            $(this).toggleClass("rotate-icon");            $(".SummaryQuotationDetail").toggle();          });          // появление прокрутки в начало страницы          $(document).ready(function () {            $(window).scroll(function () {              if ($(this).scrollTop() >= 500) {                $('.scroll-up').addClass("show");              } else {                $('.scroll-up').removeClass("show");                ;              }            });            $('.scroll-up').click(function () {              $('html, body').animate({scrollTop: 0}, 'slow');            });          });          // показать меню на мобильных устройствах          $(".line-icon-bars").click(function () {            $(".nav-mobile").show();            $("#searchBar").hide();          });          // закрыть мекню на мобильных устройствах          $(".close-btn").click(function () {            $(".nav-mobile").hide();            $("#searchBar").show();          });          // открыть/закрыть подменю на мобильных устройствах          $(".collapsed button").click(function () {            $(this).next(".submenu-list").slideToggle();            $(this).find(".icon-toggle").toggleClass("rotate-arrow");          });          // показать поиск в меню на мобильных устройствах          $(".search-link").click(function () {            $(".nav-mobile").hide();            $("#searchBar").show();            $('#fullscreen-hoc').removeClass("hidden");            $('#fullscreen-hoc').addClass("open");          });          // на мобильных устройствах показать темное лого          $(document).ready(function () {            // Проверяем ширину окна при загрузке страницы            if ($(window).width() < 1024) {              $(".logo-figure .white").hide();              $(".logo-figure .black").show();            } else {              $(".logo-figure .white").show();              $(".logo-figure .black").hide();            }          });          // закрыть полосу уведомлений          $(".error-title").click(function () {            $(".flashMessage").hide();          });          // закрыть окно  сессии          $(".Alert__close").click(function () {            $(".Alert").hide();          });          // открыть читать далее в размещении          $(".Lodging__leftSide").click(function () {            $(".room-modal").show();          });          $(".room-modal .Modal__btn-close").click(function () {            $(".room-modal").hide();          });        </script>        <!-- slick slider -->        <script type="text/javascript" src="/js/slick.min.js"></script>        <script>          $(document).ready(function () {            $(".Lodging__leftSide").click(function () {              $('.modal-room-slider').slick();            });          });        </script>        <script src="../js/intlTel.js"></script>      </client-only>    </div>  </div></template><script>import CookieBanner from "../components/banners/CookieBanner.vue";import FooterAll from "../components/Footer.vue";import Reminder from "../components/modals/Reminder.vue";import HeaderAll from "../components/HeaderAll.vue";import HeaderContact from "../components/HeaderContact.vue";import VoyageSpinner from "/components/all/voyage_spinner.vue";export default ({  components: {HeaderAll, Reminder, FooterAll, CookieBanner, HeaderContact, VoyageSpinner},  data() {    return {      load: false    }  },  head() {    return {      title: 'Туры в эксклюзивные отели по супер ценам',      link: [        {rel: 'stylesheet', type: 'text/css', href: '/css/partners.css'},        {rel: 'stylesheet', type: 'text/css', href: '/css/common.css'},        {rel: 'stylesheet', type: 'text/css', href: '/css/slick.css'},        {rel: 'stylesheet', type: 'text/css', href: '/css/help.css'},        {rel: 'stylesheet', type: 'text/css', href: '/css/order.css'},        {rel: 'stylesheet', type: 'text/css', href: '/css/account.css'},        {rel: 'stylesheet', type: 'text/css', href: '/css/gift.css'},        {rel: 'stylesheet', type: 'text/css', href: '/css/step.css'},        {rel: 'stylesheet', type: 'text/css', href: '/css/style.css'},        {rel: 'stylesheet', type: 'text/css', href: '/css/media.css'},      ],    }  },  mounted() {    setTimeout(() => {      this.load = true;    }, 200)    var stylesToRemove = ["/css/common.css", "/css/slick.css"]    stylesToRemove.forEach((href) => {      this.$metaInfo.link = this.$metaInfo.link.filter(link => link.href !== href)      var linkElement = document.querySelector('link[href="' + href + '"]');      if (linkElement) {        linkElement.parentNode.removeChild(linkElement);      }    });  },  updated() {    if (!this.load)      setTimeout(() => {        this.load = true;      }, 300)  }})</script><style scoped></style>