<template>  <section id="page-container" class="logged favorites-favorites account-index">    <div class="page-center">      <section id="wrapper_container" class="page_content haveAsideMenu"               style="justify-content: flex-end; margin-top: 10px;">        <a href="/"           @click="$cookies.remove('token')"           class="button button--secondary send-edit"><span>Выход</span>        </a>      </section>    </div>    <div class="page-center">      <section id="wrapper_container" class="page_content haveAsideMenu">        <!-- навигация страниц аккаунта -->        <aside-menu/>        <!-- содержимое страницы аккаунта -->        <main class="page-col-right">          <section class="forms-section information">            <h1 class="page-main-title">Настройки</h1>            <div id="login-details">              <div class="details">                <!--  -->                <div class="card">                  <div id="form-PasswordManager">                    <form>                      <div class="globalForm">                        <div>                          <div class="subForm">                            <header><h2 class="card__title">Данные для входа</h2></header>                            <div class="tableForm">                              <div class="FormInput email tableForm col-100">                                <div>                                  <div class="email tableForm col-100">                                    <span class="label">Email</span>                                    <div class="FormInput">                                      <input type="email"                                             placeholder=""                                             v-model="email"                                             :disabled="isDisabled">                                    </div>                                  </div>                                  <div class="FormError"></div>                                </div>                              </div>                              <div class="FormInput password tableForm col-100">                                <div>                                  <div class="password tableForm">                                    <span class="label">Старый пароль</span>                                    <div class="password-container">                                      <div class="FormInput">                                        <input type="password"                                               placeholder=""                                               autocomplete="old_password"                                               v-model="old_password">                                      </div>                                    </div>                                  </div>                                </div>                              </div>                              <div class="FormInput password tableForm">                                <div>                                  <div class="password tableForm">                                    <span class="label">Новый пароль</span>                                    <div class="password-container">                                      <div class="FormInput">                                        <input type="password"                                               placeholder=""                                               autocomplete="new-password"                                               v-model="password">                                      </div>                                    </div>                                  </div>                                </div>                              </div>                              <div class="FormInput passwordConfirm tableForm">                                <div>                                  <div class="passwordConfirm tableForm">                                    <span class="label">Повторите новый пароль*</span>                                    <div class="password-container">                                      <div class="FormInput">                                        <input type="password" placeholder="" autocomplete="no"                                               v-model="password_2">                                      </div>                                    </div>                                  </div>                                </div>                              </div>                              <div class="password-condition-container">                                <div class="Login__line--error">                                  <p v-show="password_false"><i class="icon line-icon-exclamation-circle">                                  </i>Пароль должен содержать                                    8 символов, символ верхнего регистра, цифры.</p>                                </div>                              </div>                              <div class="password-condition-container col-100">                                <p v-show="succes === 200"><i class="icon line-icon-exclamation-circle">                                </i>Пароль обновлен.</p>                                <p v-show="succes === 400"><i class="icon line-icon-exclamation-circle">                                </i>Пароль не обновился, попробуйте вести повторно.</p>                              </div>                            </div>                          </div>                        </div>                      </div>                      <button                        @click="submit"                        class="button button--secondary send-edit" type="submit">Сохранить                      </button>                      <button type="button" class="hidden">Отмена</button>                      <div class="observable"></div>                    </form>                  </div>                </div>              </div>            </div>          </section>        </main>      </section>    </div>    <div id="hubContainer"></div>  </section></template><script>import AsideMenu from "../components/menu/AsideMenu.vue";export default {  name: "AccountSettingsPage",  components: {AsideMenu},  layout: 'Account',  data() {    return {      isDisabled: true,      password: '',      old_password: '',      password_2: '',      password_2_false: '',      password_false: '',      old_password_false: '',      succes: '',      email: ''    };  },  methods: {    async submit(event) {      event.preventDefault();      if (this.$validator.isStrongPassword(this.password,        {          minLength: 8,          minLowercase: 1,          minUppercase: 1,          minNumbers: 1,          minSymbols: 0,          returnScore: false,          pointsPerUnique: 0,          pointsPerRepeat: 0,          pointsForContainingLower: 0,          pointsForContainingUpper: 0,          pointsForContainingNumber: 0,          pointsForContainingSymbol: 0        })) {        this.password_false = false;      } else {        this.password_false = true;      }      if (this.$validator.isStrongPassword(this.old_password,        {          minLength: 8,          minLowercase: 1,          minUppercase: 1,          minNumbers: 1,          minSymbols: 0,          returnScore: false,          pointsPerUnique: 0,          pointsPerRepeat: 0,          pointsForContainingLower: 0,          pointsForContainingUpper: 0,          pointsForContainingNumber: 0,          pointsForContainingSymbol: 0        })) {        this.old_password_false = false;      } else {        this.old_password_false = true;      }      if (this.$validator.isStrongPassword(this.password_2,        {          minLength: 8,          minLowercase: 1,          minUppercase: 1,          minNumbers: 1,          minSymbols: 0,          returnScore: false,          pointsPerUnique: 0,          pointsPerRepeat: 0,          pointsForContainingLower: 0,          pointsForContainingUpper: 0,          pointsForContainingNumber: 0,          pointsForContainingSymbol: 0        })) {        this.password_2_false = false;      } else {        this.password_2_false = true;      }      if (!this.password_false && !this.password_2_false && !this.old_password_false && this.password_2 === this.password) {        await this.$store.dispatch('setUpdatePassword', {          "new_password": this.password,          "old_password": this.old_password        })        this.succes = this.$store.state.status;      }    },  },  mounted() {    if (this.$route.name === 'account-settings') {      document.body.classList.add('perso', 'no-order');    }    this.email = this.$cookies.get('email');  },}</script><style scoped lang="scss">#page-container #wrapper_container.page_content {  display: flex;  justify-content: center;  flex-direction: row;}</style>