<template>  <section id="page-container" class="logged favorites-favorites">    <div class="page-center">      <section id="wrapper_container">        <main class="page-col-right">          <section id="offers-wishlist-container" class="offers-container container_12">            <!-- Сообщение если нет избранных предложений -->            <div              v-show="favorites_true"              id="no-favorite">              <section class="no-favorite">                <header class="no-favorite__header"><i class="icon icon-coeur"></i>                  <h2 class="no-favorite__header__title"> У Вас еще нет избранных отелей.</h2></header>                <div class="no-favorite__description"><p>Добавляйте понравившееся предложения в избранное чтобы не                  упустить их из виду.</p>                </div>                <footer class="no-favorite__footer"><a href="/" title=" See the flash sales " class="btn pink">                  Перейти к актуальным предложениям</a></footer>              </section>            </div>            <!-- Список избранных предложений -->            <div              v-show="!favorites_true"              id="favorites">              <h2 class="wishlist-title section-title">                <span class="wishlist-title-text">Избранное</span><em>—</em>              </h2>              <div id="bloc_wish">                <article                  v-for="item in get_hotel"                  class="sale col-4 col-tab-6 col-mob-12 ">                  <a                    :title="`${item['name']} *`"                    class="cursor_pointer"                    @click="handleClick">                    <i                      @click="deleteItem(item['id'])"                      style="z-index: 10"                      class="icon-fav-less"></i>                    <i class="delete-fav-container"></i>                    <div @click=getHref(item)>                      <figure class="sale-figure">                        <img :alt="item['name']+'*'"                             :src="item['main_image']">                      </figure>                      <div class="sale-destination sale-information">{{ item['country'] }} / {{ item['city'] }}</div>                      <div class="sale-name sale-information">{{ item['name'] }}*</div>                      <span class="sale-interstice"></span>                    </div>                  </a>                  <div class="sale-bottom">                    <i class="line-icon-clock"></i>                    <span class="sale-end" style="font-size: 10px">{{ item.sale_description.substring(0, 54) }}</span>                  </div>                </article>              </div>            </div>          </section>        </main>      </section>    </div>    <div id="hubContainer"></div>  </section></template><script>import {mapActions, mapGetters} from "vuex";export default {  name: "FavouritesPage",  layout: "Favourites",  data() {    return {      get_hotel: {},      favourites: [],      base_url: process.env.BASE_URL,    }  },  computed: {    ...mapGetters(['hotel']),    favorites_true: {      get() {        if (this.favourites)          return this.favourites.length === 0;        return true;      }    },    fvSt: {      get() {        try {          return this.$getLcStorage('favorites');        } catch (e) {          return [];        }      }    }  },  mounted() {    if (this.$route.name === 'favourites') {      document.body.classList.add('perso');    }    this.favourites = this.fvSt;    this.setHotelList();    if (this.favourites)      this.get_hotel = Object.values(this.hotel).filter(item => this.favourites.includes(`${item.id}`));  },  methods: {    ...mapActions([      'setHotelList',    ]),    handleClick(event) {      event.preventDefault();    },    getHref(sale) {      this.$nuxt.$router.push(`/sale/${sale['id']}/${sale['slug']}`);    },    deleteItem(id) {      if (this.favourites.length !== 0)        this.favourites = this.favourites.filter(item => item !== `${id}`);      this.$setLcStorage('favorites', this.favourites);      this.get_hotel = Object.values(this.hotel).filter(item => this.favourites.includes(`${item.id}`));    }  }}</script><style lang="scss">.cursor_pointer {  cursor: pointer;}</style>